import React, { useState, useEffect } from 'react';
import queryString from 'query-string';
import io from 'socket.io-client';

import './Chat.css'

let socket;
const Chat = ({ location }) => {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const EndPoint = 'localhost:5000';

    useEffect(()=>{
        const {name, room} = queryString.parse(location.search);

        socket = io(EndPoint)
        setName(name);
        setRoom(room);
        // Params : first is the event , second is the data we we want to pass, 
        //third is callback for error handling stuff 
        socket.emit('join',{name,room}, ()=>{}
        );

        return ()=>{
            // Emitting event name should be same on server side 
            socket.emit('disconnet');

            socket.off();
        }
    }, [EndPoint, location.search]);

    useEffect(()=> {
        socket.on('message', (message)=>{
            setMessages([...messages, message]);
        }, [messages]);
    });

    const sendMessage = (event)=>{
        if(message){
            socket.emit('sendMessage', message, ()=> setMessage(``));
        }
        event.preventDefault();
    }

    console.log(message, messages);

    return(
        <div className="container">
        <div className="main">
            <div className="chat">
                <div className="chat_room">
                    <div className="chat_room_info">
                        <div className="room-state"></div>
                        <h2 className="room-name">GoogleDev</h2>
                    </div> 
                    <div className="chat_room_control">
                        <div className="minimize">
                            <svg id="Layer_1" data-name="Layer 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40"><defs></defs><title>minimize</title><rect className="cls-1" width="40" height="40"/><rect x="4" y="18.5" width="32" height="3"/></svg>
                        </div>
                        <div className="maximize">
                            <svg id="Layer_1" data-name="Layer 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40"><defs></defs><title>maximize</title><rect className="cls-1" width="40" height="40"/><path id="maximize" d="M38,38H2V2H38ZM4.77,35.23H35.23V4.77H4.77Z"/></svg>
                        </div>
                        <div className="close">
                            <svg id="Layer_1" data-name="Layer 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40"><defs></defs><title>close</title><rect className="cls-1" width="40" height="40"/><rect x="18.85" y="-4.31" width="2.29" height="48.62" transform="translate(-8.28 20) rotate(-45)"/><rect x="-4.31" y="18.85" width="48.62" height="2.29" transform="translate(-8.28 20) rotate(-45)"/></svg>
                        </div>
                    </div>
                </div>
                <div className="chat_area">
                    <div className="chat_area-view">
                            <div className="user-left user-left_text">
                                <div className="name"><p>Pankaj</p></div>
                                <div className="message">Lorem ipsum dolor sit amet consectetur, adipisicing elit. Aliquid, aspernatur.</div>
                            </div>
                            <div>
                                <div className="user-right user-right_text">
                                    <div className="name"><p>Sushil</p></div>
                                    <div className="message">Yeah, tell me how not to look cool </div>
                                </div>
                            </div>

                    </div>
                </div>
                    <div className="chat_area-message">
                        <input type="text" className="type" placeholder="Lets Chat ðŸ˜ŽðŸ˜Ž"/>
                        <button className="send"><svg id="Layer_1" data-name="Layer 1" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40"><defs></defs><title>send</title><rect className="cls-1" width="40" height="40"/><polygon points="34.73 15.91 31.49 19.15 22.29 9.94 22.29 38.72 17.71 38.72 17.71 9.94 8.51 19.15 5.27 15.91 19.91 1.28 20 1.37 20.09 1.28 34.73 15.91"/></svg></button>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Chat;
